mapper.select:
select count(*) from public.t_sys_organization a left join public.t_sys_organization p on a.parent_id = p.id
a.del_flag = false
AND (a.id = #{id} OR ( a.parent_ids @> CAST(ARRAY[#{id}] AS INTEGER[])) )
AND a.code = #{code}
AND a.name like '%'||#{name}||'%'
${sqlMap.dsf}

select a.*,p.name as parent_name
from
    public.t_sys_organization a left join public.t_sys_organization p on a.parent_id = p.id

    a.del_flag = false

        AND (a.id = #{id} OR ( a.parent_ids @> CAST(ARRAY[#{id}] AS INTEGER[]) ) )


        AND a.code = #{code}


        AND a.name like '%'||#{name}||'%'

    ${sqlMap.dsf}



        ORDER BY ${page.orderBy}


        ORDER BY a.id ASC



    offset ${page.start} limit ${page.pageSize}
